package de.lendico.contoller;import com.fasterxml.jackson.databind.util.JSONPObject;import de.lendico.dto.LoanRequest;import de.lendico.services.LoanService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ExceptionHandler;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestBody;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.web.bind.annotation.RestController;import javax.validation.Valid;import lombok.RequiredArgsConstructor;@RestController@RequestMapping(path = "/")@RequiredArgsConstructorpublic class LoanController {    @Autowired    private LoanService loanService;    @PostMapping("generate-plan")    public @ResponseBody ResponseEntity<?> generatePlan(@Valid @RequestBody LoanRequest loanRequest){        if(loanRequest.validate()){            return new ResponseEntity<>("Invalid Values", HttpStatus.BAD_REQUEST);        }        return new ResponseEntity<>(loanService.calculateLoan(loanRequest), HttpStatus.OK);    }}