package de.lendico.dto;import javax.validation.constraints.NotNull;import lombok.Data;import lombok.Getter;import lombok.Setter;@Data@Getter@Setterpublic class LoanRequest {    @NotNull(message = "Loan amount should not be empty")    private String loanAmount;    @NotNull(message = "Nominal Rate should not be empty")    private String nominalRate;    @NotNull(message = "Duration should not be empty")    private Long duration;    @NotNull(message = "State date should not be empty")    private String startDate;    public boolean validate() {        return Double.parseDouble(loanAmount) <= 0 || Double.parseDouble(nominalRate) <= 0 || duration <= 0;    }}